    fullPath: "mygroup",
});
const groupProjects = mygroup.then(mygroup => gitlab.getProjects({
    groupId: mygroup.id,
    orderBy: "name",
    includeSubgroups: true,
    withShared: false,
}));
const projects = gitlab.getProjects({
    search: "postgresql",
    visibility: "private",
});
```
