		opt0 := "2019-01-01"
		opt1 := "name"
		opt2 := "desc"
		_, err := gitlab.GetUsers(ctx, &gitlab.GetUsersArgs{
			CreatedBefore: &opt0,
			OrderBy:       &opt1,
			Sort:          &opt2,
		}, nil)
