    content=my_runner.authentication_token.apply(lambda authentication_token: f"""  concurrent = 1

  [[runners]]
    name = "Hello Terraform"
    url = "https://example.gitlab.com/"
    token = "{authentication_token}"
    executor = "shell"

"""))
